<?php
namespace Tests;

use PHPUnit\Framework\TestCase;
use Code;

class SumStringsAsNumbersTest extends TestCase
{
    private static $service = null;
 
    public static function setUpBeforeClass()
    {
        self::$service = new Code\SumStringsAsNumbers();
    }
    
    public function testSimpleExamples()
    {
        $this->assertEquals("579", self::$service->sumStrings("123", "456"));
    }

    public function testFixed()
    {
        $this->assertEquals("8842", self::$service->sumStrings("8797", "45"));
    }

    public function testRandom()
    {
        $this->assertEquals(
            ""
            , self::$service->sumStrings(
                "", 
                "980065176632303225063197898002101217724488728040657067756667887909681130002916982788464021819509577680791982680859705217399259107966035133401597467988727642663359963577917076813502474138405731737184"
            )
        );
    }
}
